mapper.select:
  	SELECT 
	  `project`.* ,student.*,teacher.*
	FROM
	  `project`,
	  	student,
	  	teacher
	WHERE (student.sid = #{sid} 
	  OR stu1 = ${snumber} 
	  OR stu2 = ${snumber} 
	  OR stu3 = ${snumber} 
	  OR stu4 = ${snumber} 
	  OR stu5 = ${snumber} 
	  OR stu6 = ${snumber} ) and student.sid=project.sid and teacher.tid=project.tid 
	  order by theyear desc

  	select 
	  project.*,
	  student.*,
	  teacher.* 
  	 from  
  	  project,
	  student,
	  teacher
    
      teacher.tid=#{tid}
      
        and ischange = #{ischange}
      
      
        and changestatus = #{changestatus}
      
      
        and isremove = #{isremove}
      
      
        and removestatus = #{removestatus}
      
       
        and isissue = #{isissue}
      
      
        and isteam = #{isteam}
      
      
        and tostatus = #{tostatus}
      
      AND project.`tid` = teacher.`tid`
 	 AND student.`sid` = project.`sid`

  	SELECT 
